import { TestBed } from '@angular/core/testing';

import { PaymentService } from './payment.service';
import { CardDetails } from './card-details';
import { HttpErrorResponse } from '@angular/common/http';
import { CardPaymentResponse } from './card-payment-response';
import { BillingDetails } from './billing-details';

describe('PaymentService', () => {
  let service: PaymentService;
  const defaultTimeout = 5000;

  beforeEach(() => {
      jasmine.DEFAULT_TIMEOUT_INTERVAL = defaultTimeout;
      TestBed.configureTestingModule({});
    service = TestBed.inject(PaymentService);
  });

  it('should be created', () => {
    expect(service).toBeTruthy();
  });

  it('should fetch the rsa public 4key from Circle', async () => {
      const rsaPublicKey = await service.getPCIPublicKey();
      // eslint-disable-next-line max-len
      const expectedPublicKey = '';
      expect(rsaPublicKey.publicKey).toBe(expectedPublicKey);
  });

  it('should properly encrypt a message', async () => {
     const cardNumber = '4100 4000 1234 5678';
     const cvv = '123';
     const cardDetails = new CardDetails(cardNumber, cvv);
     const encryptedValue = await service.encryptCardData(cardDetails);

     expect(encryptedValue).toBeTruthy();
  });

  it('should throw an error when bad data is passed to create card', async () => {
      jasmine.DEFAULT_TIMEOUT_INTERVAL = 10000;
      const cardNumber = '';
      const cvv = '';
      const expectedException = new HttpErrorResponse({
        status: 500,
        statusText: 'Failed to create card'
      });

      return expectAsync(service.createCard(new CardDetails(cardNumber, cvv),
      new BillingDetails(
        'Satoshi Nakamoto',
        'Boston',
        'US',
        '100 Money Street',
        'Suite 1',
        'MA',
        '01234',
        10,
        1,
        2020
        )
      )).toBeRejectedWith(expectedException);
  });

  it('should successfully create a card and get an id back', async () => {
      const cardNumber = '4007400000000007';
      const cvv = '123';
      const cardDetails = new CardDetails(cardNumber, cvv);
      const billingDetails = new BillingDetails(
        'Satoshi Nakamoto',
        'Boston',
        'US',
        '100 Money Street',
        'Suite 1',
        'MA',
        '01234',
        10,
        1,
        2020
        );
      const result: CardPaymentResponse = await service.createCard(cardDetails,billingDetails);

      expect(result).toBeTruthy();
      expect(result.last4).toBe('0007');
      expect(result.id).toBeTruthy();
      expect(result.bin).toBe('400740');
  });
});
